<?php//create a general task-level permission for admins$this->_authManager->createTask("adminManagement", "access to theapplication administration functionality");//create the site admin role, and add the appropriate permissions$role=$this->_authManager->createRole("admin");$role->addChild("owner");$role->addChild("reader");$role->addChild("member");$role->addChild("adminManagement");//ensure we have one admin in the system (force it to be user id #1)$this->_authManager->assign("admin",1);?>